import React from 'react';

const About: React.FC = () => {
  return (
    <div className="max-w-3xl mx-auto space-y-8">
      <h2 className="text-4xl font-bold mb-6 text-center text-gray-800">About Incredible India Travel</h2>
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <p className="text-gray-600 mb-4">
          Incredible India Travel is your gateway to exploring the diverse and vibrant culture of India. We are passionate about connecting travelers with the heart and soul of this magnificent country.
        </p>
        <p className="text-gray-600 mb-4">
          Our platform allows you to plan your dream Indian adventure and connect with fellow travelers. Whether you're drawn to the bustling streets of Mumbai, the serene backwaters of Kerala, or the majestic Himalayas, we're here to make your journey unforgettable.
        </p>
        <p className="text-gray-600">
          Join our community of explorers and embark on a journey that will captivate your senses and leave you with memories to cherish for a lifetime.
        </p>
      </div>
    </div>
  );
};

export default About;
